    const [count,setCount] = useState(0)
    useEffect(() => {
      console.log("Mounting................")

      return () => {
        console.log("Unmounting.................")
      }
    }, [third])




    const addCount=()=>{
        setCount(count + 1)
    }
    let obj = {
        name:'1st Counter',
        data:count
    }
    let dev = [
        {name:"madhan", age:24},
        {name:"ganesh", age:21},
        {name:"aravind", age:'3 months'},
        {name:"vignesh", age:22},

    ]

    return(
        <div>
            <CounterDisplay name='1st Counter' data={count}/>
            {/* <CounterDisplay {...obj}/> */}
            <CounterDisplay name='2nd Counter' data={(count+1)*2-1}/>
            {
                dev.map((obj, idx)=>{
                    return(
                        <Developers key={idx} {...obj}/>
                    )
                })
            }

            {/* <h2>Counter = {count} </h2> */}
            <button onClick={addCount}>Add Count</button>
        </div>
    )
}


export default Counter
